<?php

$input = [
    '',
];

$found = [];
$charsToCheck = 14;
foreach ($input as $stream) {
    $stream = str_split($stream);
    $lastChars = array_splice($stream, 0, $charsToCheck);
    $counter = $charsToCheck;

    while (count(array_unique($lastChars)) !== $charsToCheck) {
        array_shift($lastChars);
        $char = array_shift($stream);
        $lastChars[] = $char;

        $counter++;
    }

    $found[] = $counter;
}


die(var_dump(
    $found
));
