<?php

declare(strict_types=1);

namespace DarkaOnLine\AdventOfCode2022;

use Symfony\Component\Console\Command\Command;
use Symfony\Component\Console\Input\InputInterface;
use Symfony\Component\Console\Output\OutputInterface;

class Game6 extends Command
{
    protected static $defaultName = 'game:6';

    protected function execute(InputInterface $input, OutputInterface $output)
    {
        $input = [
            '',
        ];

        $found = [];
        $charsToCheck = 14;
        foreach ($input as $stream) {
            $stream = str_split($stream);
            $lastChars = array_splice($stream, 0, $charsToCheck);
            $counter = $charsToCheck;

            while (count(array_unique($lastChars)) !== $charsToCheck) {
                array_shift($lastChars);
                $char = array_shift($stream);
                $lastChars[] = $char;

                $counter++;
            }

            $found[] = $counter;
        }


        dd($found);
    }
}
